#include <stdint.h>

#include <zephyr/arch/arm/aarch32/irq.h>
#include <zephyr/arch/cpu.h>
#include <zephyr/devicetree.h>
#include <zephyr/drivers/interrupt_controller/intc_vim.h>
#include <zephyr/kernel.h>
#include <zephyr/logging/log.h>
#include <zephyr/sys/util_macro.h>

LOG_MODULE_REGISTER(vim);
```', 'comment_created': datetime.datetime(2023, 8, 30, 20, 3, 26, tzinfo=datetime.timezone.utc), 'commenter': 'cfriedt', 'type': 'User'}]

